import React, {useState} from "react";
import Loader from "./utils/Loader";
import Form from "./form"
import {
  isAccountVerified,
  verifyAccount,
  createProfile,
  getusersList
} from "./utils/functions";


const Functions = () => {
  const [verificationType, SetVerificationType] = useState("?");
  const [Users, setUsers] = useState([]);

  const [userId, setUserId] = useState("None");
  const [loading, setLoading] = useState(false);

  const getVerification = async (userId) => {
    try {
      setLoading(true);
      SetVerificationType(await SetVerificationType(isAccountVerified(userId)));
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }
  };

  const createNewProfile = async()=> {
    try {
      setLoading(true);
      setUserId(await (createProfile()));
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }  
};

const getUsers = async () => {
  try {
    setLoading(true);
    array = await getusersList()
    setUsers(arr => [...arr,array]);
  } catch (error) {
    console.log(error);
  } finally {
    setLoading(false);
  }
};

const addVerification = async (userId) => {
  try {
    setLoading(true);
    await verifyAccount({userId});
  } catch (error) {
    console.log(error);
  } finally {
    setLoading(false);
  }
};
return (
  <>
    {!loading ? (
        <>
          <button onClick={createNewProfile}>Create Account</button>
          <button onClick={getUsers}>Get users</button>
          <h1> user : {Users}</h1>
        </>
    ) : (
      <Loader />
    )}
  </>
);
};

export default Functions;
