import React, {useState, useEffect} from "react";
import Loader from "./utils/Loader";
import Form from "./form"
import {
  isAccountVerified,
  verifyAccount,
  createProfile,
  getusersList
} from "./utils/functions";


const Functions = () => {
  const [verificationType, SetVerificationType] = useState("None");
  const [Users, setUsers] = useState([]);
  const [userId, setUserId] = useState("None");
  const [loading, setLoading] = useState(false);

  const getVerification = async (userId) => {
    try {
      setLoading(true);
      SetVerificationType(await isAccountVerified(userId));
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }
  };

  const createNewProfile = async()=> {
    try {
      setLoading(true);
      setUserId(window.walletConnection.account().accountId)
      setUserId(await (createProfile()));
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }  
};

const getUsers = async () => {
  try {
    setLoading(true);
    setUsers(await getusersList())
  } catch (error) {
    console.log(error);
  } finally {
    setLoading(false);
  }
};

const addVerification = async (userId , type) => {
  try {
    setLoading(true);
    await verifyAccount(userId, type);
  } catch (error) {
    console.log(error);
  } finally {
    setLoading(false);
  };
}
  useEffect(() => {
    createNewProfile()
  }, []);

return (
  <>
    {!loading ? (
        <>
          <button onClick={() => getVerification({userId})}>Get verification</button>
          <button onClick={() => addVerification({userId} , 1).then(getVerification({userId} ))}>Add verification</button>
          <div>userId : {userId}</div>
          <div>Verification Type : {verificationType}</div>
        </>
    ) : (
      <Loader />
    )}
  </>
);
};

export default Functions;
