import React, {useState, useEffect} from "react";
import Form from "./form" 


const Direct = ({ID}) => {
  const [vType, SetVerificationType] = useState("type");
  const getVerification = async (userId) => {
    try {
      setLoading(true);
      SetVerificationType(await isAccountVerified(userId));
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }
  };
  useEffect(async() => {
    await getVerification(ID)
  }, []);
  if(vType === "New")
  {
    return (
        <>
        <Form userID = {ID}/>
        </>
      );
  }
  else if(vType === "Spam")
  {
    return (
      <>
      <h1> {ID} is marked as a spam account</h1>
      </>
    );
  }
  else if(vType === "Pending")
  {
    return (
      <>
      <h1> {ID} is Pending verification</h1>
      </>
    );
  }
  else if(vType === "Verified")
  {
    return (
      <>
      <h1> {ID} is verified successfully</h1>
      </>
    );
  }
  else if(vType === "Rejected")
  {
    return (
      <>
      <h1> {ID} verification rejected</h1>
      </>
    );
  }
  return <h1> Error : {vType}</h1>;
};
export default Direct;
